return items.map(item => {
  const fecha = new Date(item.json.fecha);

  const fechaInicio = new Date(fecha);

  // Función para formatear fecha + horas
  function formatFechaConHoras(fecha) {
    return fecha.toISOString().slice(0, 19).replace('T', ' ');
  }

  function fechaFinal(fecha) {
    return (fecha.getHours() + ':' + fecha.getMinutes().toString().padStart(2,'0') + ':' + fecha.getSeconds().toString().padStart(2,'0'));
  }

  return {
    json: {
      ...item.json,
      fechaInicio: formatFechaConHoras(fechaInicio),
      fechaFinal: fechaFinal(new Date(fechaInicio.getTime() + 30*60*1000)) // +30 min
    }
  };
});

// Obtener la fecha y hora del usuario desde el webhook
const fechaUsuario = $input.first().json.body.conversation.timestamp.toDateTime('s').toLocal();

// Función verificar solapamiento (horas solicitadas vs eventos)
function verificarSolapamiento(eventoInicio, eventoFin, fechaSolicitada) {
  const inicio = new Date(eventoInicio);
  const fin = new Date(eventoFin);
  const solicitada = new Date(fechaSolicitada);

  return solicitada >= inicio && solicitada <= fin;
}

// Obtener todos los eventos del calendario (simula Google Calendar API)
const todosEventos = [
  {
    start: "2026-01-12T10:00:00",
    end: "2026-01-12T11:00:00",
    summary: "Reunión equipo",
    startDateTime: "2026-01-12 10:00"
  },
  {
    start: "2026-01-12T14:00:00", 
    end: "2026-01-12T15:30:00",
    summary: "Llamada cliente",
    startDateTime: "2026-01-12 14:00"
  }
  // ... más eventos
];

// Filtrar eventos coincidentes
const eventosCoincidentes = todosEventos.filter(evento => 
  verificarSolapamiento(evento.start, evento.end, fechaUsuario)
);

// Normalizar fecha para respuesta (formato legible)
const fechaNormalizada = fechaUsuario.toFormat('yyyy-MM-dd HH:mm:ss');

// Verificar disponibilidad
const eventosCoincidentesCount = eventosCoincidentes.length === 0;

// Preparar respuesta
const respuesta = {
  eventosEncontrados: todosEventos.length,
  eventosCoincidentes: eventosCoincidentes,
  fechaSolicitada: fechaUsuario.toISO(),
  fechaNormalizada: fechaNormalizada,
  disponible: eventosCoincidentesCount,
  mensajeUsuario: eventosCoincidentesCount ? 
    "No hay eventos solapados. ¡Disponible!" : 
    `Hay ${eventosCoincidentes.length} eventos en conflicto`
};

// Devolver la respuesta como un nuevo item
return [{ json: respuesta }];

